    private static void registerItem(String name, Item item) {
        Registry.register(Registries.ITEM, new Identifier(Main.MOD_ID, name), item);
    }

    private static void registerItem(float compost, String name, Item item) {
        Registry.register(Registries.ITEM, new Identifier(Main.MOD_ID, name), item);
        CompostingChanceRegistry.INSTANCE.add(item, compost);
    }

